mapper.update:
UPDATE
    users
SET
    last_name = #{lastName}
    ,first_name = #{firstName}
    ,authority_no = #{authorityNo}
    ,email = #{email}
    ,password = #{password}
    ,last_login_at = CURRENT_TIMESTAMP
    ,updated_user_id = #{updatedUserId}
    ,updated_at = CURRENT_TIMESTAMP
WHERE
    user_id = #{userId}
    AND delete_flag = 0
    AND updated_at = #{updated_at}
